import axios from 'axios';
import React, { useState } from 'react'
import MiniModalLeft from './components/MiniModalLeft';
import MiniModalRight from './components/MiniModalRight';

// axios.post('http://localhost:8888/api/addUser', inputs)
        // .then(function(response){
        //     console.log(response);
        // });

const Register = () => {

    const [inputs, setInputs] = useState({
        first: '',
        last: '', 
        email: '',
        username: '', 
        contact: '',
        password: '',
    });

    const [nameError, setNameError] = useState();
    const [lastError, setLastError] = useState();
    const [emailError, setEmailError] = useState();
    const [usernameError, setUsernameError] = useState();
    const [contactError, setContactError] = useState();
    const [passwordError, setPasswordError] = useState();
    const [passwordConError, setPasswordConError] = useState();

    const firstVal = (e) => {
        const value = e.target.value;
        setInputs({...inputs, first: value});

        if(inputs.first != ''){
            setNameError();
        } 
    }

    const lastVal = (e) => {
        const value = e.target.value;
        setInputs({...inputs, last: value});

        if(inputs.last != ''){
            setLastError();
        } 
    }

    const emailVal = (e) => {
        const value = e.target.value;
        setInputs({...inputs, email: value});

        if(inputs.email != ''){
            setEmailError();
        } 
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const value = e.target.value;
        console.log(inputs);

        if(inputs.first === ''){
            setNameError(<MiniModalLeft message="FUCK!!" />);
        } else {
            setNameError();
        }

        if(inputs.last === ''){
            setLastError(<MiniModalRight message="FUCK!!" />);
        } else {
            setLastError();
        }

        if(inputs.email === ''){
            setEmailError(<MiniModalRight message="FUCK!!" />);
        } else {
            setEmailError();
        }
    }



  return (
    <div>
        <form>
            <h1>Sign Up to FakeBook</h1>
            <p>Give us all that sweet sweet data!</p>
            <div className='names'>
                {nameError}
                <input name="first" className='left' type="text" placeholder='First Name' onChange={firstVal} />
                {lastError}
                <input name='last' type="text" placeholder='Last Name' onChange={lastVal} />
            </div>
            {emailError}
            <input name="email" type="email" placeholder='Your Email' />
            <div className='userCon'>
               {usernameError}
                <input name="username" className='left' type="username" placeholder='Select A Username' />
                {contactError}
                <input name="contact" type="contact" placeholder='Contact Number' />
            </div>
            <div className='passCon'>
                {passwordError}
                <input name="password" type="password" placeholder='Choose A Password' />
                {passwordConError}
                <input name="conPass" type="password" placeholder='Confirm Password' />
            </div>
            <button type="submit" onClick={handleSubmit} >Take My Data Lizard Man!</button>
        </form>

    </div>
  )
}

export default Register
