import React from 'react'
import axios from 'axios'
import { useState, useEffect } from 'react'
import PostItem from './components/PostItem'


const Dashboard = () => {

    const [userId, setUserId] = useState({
        activeUser: sessionStorage.getItem('activeUser'),
    });

    const [posts, setPosts] = useState(<PostItem />);
    const [postMessage, setPostMessage] = useState({
      message: ''
    });

    useEffect(()=>{

      axios.post('http://localhost:8888/api/readUserPosts.php', userId)
      .then((res)=>{
        let data = res.data;
        console.log(data);

        //const postData = [];

        // for(let i = 0; i < data.length; i++){
        //   postData.push({
        //       id: data[i].id,
        //       userpost: data[i].userpost,
        //       timestamp: data[i].timestamp,
        //       message: data[i].message
        //   });
        // }

        let renderPost = data.map((item) =>  <PostItem userpost={item.userpost} date={item.timestamp} message={item.message}  />);

        setPosts(renderPost);

      })
      .catch(err=>{
        console.log(err);
      });

   },[]);

   const postVal = (e) => {
    let messageVal = e.target.value;
    setPostMessage({...postMessage, message: messageVal});
    console.log(postMessage);
   }

   const addNewPost = (e) => {
     e.preventDefault();

     axios.post('http://localhost:8888/api/addPost.php', postMessage)
      .then((res)=>{
        let data = res.data;
        console.log(data); 
      });
   }




  return (
    <div>
      <h1>Your Timeline</h1>
      <p>Lets create a library of cringe</p>
      <div className='posts'>
        {posts}
      </div>
      <div className='form'>
        <form>
          <p>Add New Post</p>
          <textarea placeholder='New Post Message' onChange={postVal} />
          <button type='submit' onClick={addNewPost}>Add Post To Timeline</button>
        </form>
      </div>

    </div>
  )
}

export default Dashboard
