import React from 'react'
import axios from 'axios'
import { useState, useEffect } from 'react'
import PostItem from './components/PostItem'


const Dashboard = () => {

    const [userId, setUserId] = useState({
        activeUser: sessionStorage.getItem('activeUser'),
    });

    const [posts, setPosts] = useState(<PostItem />);

    useEffect(()=>{

      axios.post('http://localhost:8888/api/readUserPosts.php', userId)
      .then((res)=>{
        let data = res.data;
        console.log(data);

        const postData = [];

        for(let i = 0; i < data.length; i++){
          postData.push({
              id: data[i].id,
              userpost: data[i].userpost,
              timestamp: data[i].timestamp,
              message: data[i].message
          });
        }

        //let renderPost = data.map((item) =>  <PostItem userpost={data.userpost} date={data.timestamp} message={data.message}  />);

        //setPosts(renderPost);

      })
      .catch(err=>{
        console.log(err);
      });

   },[]);




  return (
    <div>
      <h1>Your Timeline</h1>
      <p>Lets create a library of cringe</p>
    {posts}
    </div>
  )
}

export default Dashboard
