import axios from 'axios';
import React, { useState } from 'react'
import ErrorAsset from './assets/error.svg';
import MiniModalLeft from './components/MiniModalLeft';

const Register = () => {

    const [inputs, setInputs] = useState({});

    const [nameError, setNameError] = useState();
    const [lastError, setLastError] = useState();
    const [emailError, setEmailError] = useState();
    const [usernameError, setUsernameError] = useState();
    const [contactError, setContactError] = useState();
    const [passwordError, setPasswordError] = useState();
    const [passwordConError, setPasswordConError] = useState();

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(inputs);

        const isEmpty = Object.keys(inputs).length === 0;

       if(isEmpty === true){
           setNameError(<MiniModalLeft message="You Have a Name Right?"/>);
           setEmailError(<MiniModalLeft message="You Must Enter An Email?"/>);
           setUsernameError(<MiniModalLeft message="Choose A Username?"/>);
           setPasswordError(<MiniModalLeft message="Please Enter A Password?"/>); 
       } 

       setPasswordConError(<MiniModalLeft message="Your Passwords Do Not Match"/>);








        // axios.post('http://localhost:8888/api/addUser', inputs)
        // .then(function(response){
        //     console.log(response);
        // });
    }

  return (
    <div>
        <form>
            <h1>Sign Up to FakeBook</h1>
            <p>Give us all that sweet sweet data!</p>

            <div className='names'>
                {nameError}
                <input name="first" className='left' type="text" placeholder='First Name' onChange={handleChange} />
                <div className='alertMiniModalRight'>
                    <img src={ErrorAsset} />
                    <p>What's Your Last Name</p>
                </div>
                <input name='last' type="text" placeholder='Last Name'onChange={handleChange}  />
            </div>
            {emailError}
            <input name="email" type="email" placeholder='Your Email' onChange={handleChange} />
            <div className='userCon'>
               {usernameError}
                <input name="username" className='left' type="username" placeholder='Select A Username' onChange={handleChange}/>
                <div className='alertMiniModalRight'>
                    <img src={ErrorAsset} />
                    <p>Can I Have Your Number...</p> 
                </div>
                <input name="contact" type="contact" placeholder='Contact Number' onChange={handleChange}/>
            </div>
            <div className='passCon'>
                {passwordError}
                <input name="password" type="username" placeholder='Choose A Password' onChange={handleChange}/>
                {passwordConError}
                <input type="password" placeholder='Confirm Password' onChange={handleChange}/>
            </div>
            <button type="submit" onClick={handleSubmit}>Take My Data Lizard Man!</button>
        </form>

    </div>
  )
}

export default Register
